import { useState } from "react";
import axios from "axios";

interface WeatherData {
  current_condition: {
    temp_C: string;
    weatherDesc: { value: string }[];
  }[];
}

export default function WeatherApp() {
  const [city, setCity] = useState("");
  const [data, setData] = useState<WeatherData | null>(null);

  const handleSearch = async () => {
    try {
      const res = await axios.get(`https://wttr.in/${city}?format=j1`);
      setData(res.data);
    } catch (err) {
      alert("Không thể lấy dữ liệu thời tiết!");
    }
  };

  return (
    <div style={{ textAlign:"center", backgroundColor:"#7bb3f0ff", width:"60%", padding:"30px", margin:"0 auto" }}>
      <h1>Thời tiết</h1>
      <input
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Nhập tên thành phố..."
      />
      <button onClick={handleSearch}>Xem</button>

      {data && (
        <div style={{ marginTop: "1rem" }}>
          <h3>{city.toUpperCase()}</h3>
          <p>Nhiệt độ: {data.current_condition[0].temp_C}°C</p>
          <p>Tình trạng: {data.current_condition[0].weatherDesc[0].value}</p>
        </div>
      )}
    </div>
  );
}
