import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import WeatherApp from "./components/Weather";
import StudentList from "./components/StudentList";
import NewsApp from "./components/News";

export default function App() {
  return (
    <BrowserRouter>
      <nav
        style={{
          backgroundColor: "#add7f7ff",
          padding: "10px",
          marginBottom: "20px",
          display: "flex",
          gap: "20px", // khoảng cách giữa các link
        }}
      >
        <Link to="/" style={{ textDecoration: "none", color: "#000" }}>Weather</Link>
        <Link to="/students" style={{ textDecoration: "none", color: "#000" }}>Students</Link>
        <Link to="/news" style={{ textDecoration: "none", color: "#000" }}>News</Link>
      </nav>

      <Routes>
        <Route path="/" element={<WeatherApp />}  />
        <Route path="/students/*" element={<StudentList />} />
        <Route path="/news" element={<NewsApp />} />
      </Routes>
    </BrowserRouter>
  );
}
